import React, { useState } from "react";
import "./Chatbot.css"; // Ensure you have Chatbot.css in the same directory

const Chatbot = () => {
  const [messages, setMessages] = useState([{ text: "", sender: "bot" }]);
  const [input, setInput] = useState("");

  const sendMessage = () => {
    if (input.trim() === "") return;
    setMessages([...messages, { text: input, sender: "user" }]);
    setTimeout(() => {
      setMessages((prev) => [
        ...prev,
        { text: "I'm just a bot! ðŸ˜Š How else can I help?", sender: "bot" },
      ]);
    }, 1000); // Simulating bot response delay
    setInput("");
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-header">CHATTIX</div>
      <div className="chatbot-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`chat-message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          placeholder="Type a message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
