import React, { useEffect } from "react";
import "./Home.css"; // Ensure CSS file is properly imported

const Home = () => {
  // Function to open chatbot in a new full-screen tab
  const openChatbot = () => {
    window.open("/chatbot", "_blank"); // Opens chatbot in a new full-screen tab
  };

  useEffect(() => {
    const sections = document.querySelectorAll(".section");
    let lastScrollTime = 0;

    const handleScroll = (event) => {
      const now = new Date().getTime();
      if (now - lastScrollTime < 1000) return; // Prevents fast scrolling

      lastScrollTime = now;
      let closestSection = null;
      let minDistance = Infinity;

      sections.forEach((section) => {
        const rect = section.getBoundingClientRect();
        const distance = Math.abs(rect.top);

        if (distance < minDistance) {
          minDistance = distance;
          closestSection = section;
        }
      });

      if (closestSection) {
        closestSection.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="home">
      {/* Landing Section */}
      <section
        className="landing section"
        style={{ backgroundImage: 'url("/assets/bk2.png")' }}
      >
        <h1>WELCOME TO MUSEUM TICKET BOOKING</h1>
      </section>

      {/* Explore Museums Section */}
      <section className="explore section">
        <h2>EXPLORE MUSEUM</h2>
        <div className="museum-gallery">
          <div className="museum-item">
            <img src="/assets/national_m.jpg" alt="National Museum" />
            <div className="museum-info">
              <p>National Museum</p>
              <p>New Delhi</p>
            </div>
          </div>
          <div className="museum-item">
            <img src="/assets/police_m.jpg" alt="Police Museum" />
            <div className="museum-info">
              <p>Police Museum</p>
              <p>Chennai</p>
            </div>
          </div>
          <div className="museum-item">
            <img src="/assets/seashell_m.jpg" alt="Seashell Museum" />
            <div className="museum-info">
              <p>Seashell Museum</p>
              <p>Mahabalipuram</p>
            </div>
          </div>
        </div>
      </section>

      {/* Chatbot Icon */}
      <div className="chatbot-icon" onClick={openChatbot}>
        <img src="/assets/chat_icon.jpg" alt="Chatbot" />
        <span className="tooltip">
          I'm your AI assistant for ticket booking
        </span>
      </div>
    </div>
  );
};

export default Home;
